// import React from 'react';
import React, { useState, useRef } from "react";

import Editor from "@monaco-editor/react";
import useLocalStorage from '../Hook/useLocalStorage';
import { CopyToClipboard } from "react-copy-to-clipboard";
import { EnableDisable } from "../Hook/EnableDisable";

function CodeEditor() {

  const [HTML, setHTML] = useLocalStorage("HTML","")
  const [CSS, setCSS] = useLocalStorage("CSS","")
  const [JS, setJS] = useLocalStorage("JS","")

  const [active, setActive] = React.useState("HTML")

  const IFRAMECODE = `
  <html>
    <head>
      <style>
        ${CSS}
      </style>
    </head>

    <body>
      ${HTML}

      <script> 
        ${JS}
      </script>
    </body>

  </html>
  `

  const [text, setText] = useState("");
  const [isCopied, setIsCopied] = useState(false);

  const copyToClip = () => {
      setIsCopied(true);
      setTimeout(() => {
          setIsCopied(false);
      }, 1000);
  }; 

  const { useRef } = React;

    const buttonRef = useRef(null);
    const handleButtonClick = () => {
      // Your button click logic here
    };

    const disableButton = () => {
      buttonRef.current.disabled = true;

    };

    const enableButton = () => {
      buttonRef.current.disabled = false;

    };

  
 
  return (
    <>
    <div style={{ padding: "20px"}}>
      <h1>Code Editor</h1>
      <div style={{ display:"flex", border:"1px solid black" }}>
     
        {/* editor */}
        <div style={{width:"100%"}} >

          <button onClick={()=>setActive("HTML")} style={{color:active==="HTML"?"red":"black"}}>HTML</button>
          <button onClick={()=>setActive("CSS")} style={{color:active==="CSS"?"red":"black"}}>CSS</button>
          <button onClick={()=>setActive("JS")} style={{color:active==="JS"?"red":"black"}}>JS</button>

          {active==="HTML" && 
          <Editor
     height="60vh"
     defaultLanguage="html"
     defaultValue={HTML}
     onChange={(value,event)=>setHTML(value)}
     ref={buttonRef} onClick={handleButtonClick}
   />
          // <textarea value={HTML} onChange={event=>setHTML(event.currentTarget.value)}></textarea>
          
          }
          {active==="CSS" && 
          <Editor

          height="60vh"
          defaultLanguage="css"
          defaultValue={CSS}
          onChange={(value,event)=>setCSS(value)}
        />

          // <textarea value={CSS}  onChange={event=>setCSS(event.currentTarget.value)}></textarea> 
          
          }
          {active==="JS" && 
          <Editor
          height="60vh"
          defaultLanguage="javascript"
          defaultValue={JS}
          onChange={(value,event)=>setJS(value)}
        />
          // <textarea value={JS} onChange={event=>setJS(event.currentTarget.value)}></textarea>
          }


        </div>

        {/* result */}
        <div style={{width:"100%", height:"66vh"}}>
        <iframe height={"100%"} width={"100%"} srcDoc={IFRAMECODE} />

        </div>
      </div>
      <div>
      <div style={{display:'flex', padding :'10px'}}>
      <CopyToClipboard text={HTML} onCopy={copyToClip}>
            <div className="copy-btn">
                <button >Copy </button>
              
            </div>
        </CopyToClipboard>
        <div className='container'>
          <h3>React Js Disable Button using useref</h3>
          <button ref={buttonRef} onClick={handleButtonClick}>
            Click Me
          </button>
          <button onClick={disableButton}>Disable Button</button>
          <button onClick={enableButton}>Enable Button</button>
        </div>
       </div>
       {isCopied &&
                    <span style={{color:'green'}}> Copied..!</span>
                }
       </div>
    </div>


</>
  );
}

export default CodeEditor;
