// import React from 'react';
import React, { useState, useRef } from "react";

import Editor from "@monaco-editor/react";
import useLocalStorage from '../Hook/useLocalStorage';
import { CopyToClipboard } from "react-copy-to-clipboard";

function CodeEditor() {

  const [HTML, setHTML] = useLocalStorage("HTML","")
  const [CSS, setCSS] = useLocalStorage("CSS","")
  const [JS, setJS] = useLocalStorage("JS","")

  const [active, setActive] = React.useState("HTML")

  const IFRAMECODE = `
  <html>
    <head>
      <style>
        ${CSS}
      </style>
    </head>

    <body>
      ${HTML}

      <script> 
        ${JS}
      </script>
    </body>

  </html>
  `

  const [text, setText] = useState("");
  const [isCopied, setIsCopied] = useState(false);

  const copyToClip = () => {
      setIsCopied(true);
      setTimeout(() => {
          setIsCopied(false);
      }, 1000);
  }; 

  
  const [isButtonDisabled, setButtonDisabled] = useState(false); 
  
  const disableButton = () => { 
      setButtonDisabled(true); 
      alert("Button has been disabled!"); 
  }; 

  const enableButton = () => { 
      setButtonDisabled(false); 
      alert("Button has been enabled!"); 
  }; 
  return (
    <>
    <div style={{ padding: "20px"}}>
      <h1>Code Editor</h1>
      <div style={{ display:"flex", border:"1px solid black" }}>
        {/* editor */}
        <div style={{width:"100%"}}>
          <button onClick={()=>setActive("HTML")} style={{color:active==="HTML"?"red":"black"}} >HTML</button>
          <button onClick={()=>setActive("CSS")} style={{color:active==="CSS"?"red":"black"}}>CSS</button>
          <button onClick={()=>setActive("JS")} style={{color:active==="JS"?"red":"black"}}>JS</button>

          {active==="HTML" && 
          <Editor
     height="60vh"
     defaultLanguage="html"
     defaultValue={HTML}
     onChange={(value,event)=>setHTML(value)}
   />
          // <textarea value={HTML} onChange={event=>setHTML(event.currentTarget.value)}></textarea>
          
          }
          {active==="CSS" && 
          <Editor
          height="60vh"
          defaultLanguage="css"
          defaultValue={CSS}
          onChange={(value,event)=>setCSS(value)}
        />

          // <textarea value={CSS}  onChange={event=>setCSS(event.currentTarget.value)}></textarea> 
          
          }
          {active==="JS" && 
          <Editor
          height="60vh"
          defaultLanguage="javascript"
          defaultValue={JS}
          onChange={(value,event)=>setJS(value)}
        />
          // <textarea value={JS} onChange={event=>setJS(event.currentTarget.value)}></textarea>
          }


        </div>

        {/* result */}
        <div style={{width:"100%", height:"66vh"}}>
        <iframe height={"100%"} width={"100%"} srcDoc={IFRAMECODE} />

        </div>
      </div>
      <CopyToClipboard text={HTML} onCopy={copyToClip}>
            <div className="copy-btn">
                <button >Copy </button>
                {isCopied &&
                    <span style={{color:'green'}}> Copied..!</span>
                }
            </div>
        </CopyToClipboard>

        <button 
                onClick={disableButton} 
                style={isButtonDisabled ? 
                    styles.disabledButton : styles.enabledButton} 
                disabled={isButtonDisabled} 
            > 
                Disable 
            </button> 
            <button 
                onClick={enableButton} 
                style={!isButtonDisabled ? 
                    styles.disabledButton : styles.enabledButton} 
                disabled={!isButtonDisabled} 
            > 
                Enable 
            </button> 
    </div>


</>
  );
}

export default CodeEditor;

const styles = { 
  container: { 
      textAlign: 'center', 
      margin: 'auto', 
      padding: '20px', 
      width: 400, 
  }, 
  heading: { 
      fontSize: '34px', 
      marginBottom: '10px', 
      color: "green", 
      borderBottom: "3px solid green", 
      paddingBottom: 20, 
      borderRadius: "8px", 
  }, 
  disabledButton: { 
      backgroundColor: 'gray', 
      color: 'white', 
      cursor: 'not-allowed', 
      margin: 10, 
      padding: 15, 
      borderRadius: "8px", 
      border: "none", 
      boxShadow: "0px 0px 10px 0px grey", 
  }, 
  enabledButton: { 
      backgroundColor: 'red', 
      color: 'white', 
      cursor: 'pointer', 
      margin: 10, 
      padding: 15, 
      borderRadius: "8px", 
      border: "none", 
      boxShadow: "0px 0px 10px 0px grey", 
  }, 
};
