import React from "react";
import FileSaver from "file-saver"; // https://www.npmjs.com/package/file-saver

const drawCanvas = async () => {
  const canvas = document.createElement("canvas");
  canvas.height = 70;
  canvas.width = 200;
  const ctx = canvas.getContext("2d");
  ctx.fillStyle = "#ff0000"; // red
  ctx.fillRect(0, 35, 100, 35); // x,y,width,height
  ctx.font = "30px Arial";
  ctx.fillStyle = "black";
  ctx.fillText("Hello World", 10, 50); // text,x,y
  document.querySelector("#screenshot").append(canvas);
};



const saveTextExample = () => {
  var blob = new Blob(["Hello, world!"], { type: "text/plain;charset=utf-8" });
  FileSaver.saveAs(blob, "hello world.txt");
};


export default function SaveFile() {
  return (
    <>
      <div  id="capture">
        <h1>Hello CodecccccSandbox</h1>
        <h2>Start editing to see some magic happen!</h2>
      </div>
      <button onClick={saveTextExample}>Save text</button>
      <button onClick={drawCanvas}>Draw Canvas</button>
      <br />
      <div>
        <div>Screeshot below:</div>
        <div id="screenshot"></div>
      </div>
    </>
  );
}
